CREATE TABLE IF NOT EXISTS therapists (
    id UUID PRIMARY KEY,
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    daily_start_hour INTERVAL NOT NULL DEFAULT '08:00:00',
    daily_end_hour INTERVAL NOT NULL DEFAULT '17:00:00',
    lunch_start INTERVAL NOT NULL DEFAULT '12:00:00',
    lunch_end INTERVAL NOT NULL DEFAULT '13:00:00',
    default_appointment_duration_minutes INT NOT NULL DEFAULT 30,
    specialty VARCHAR(50) NOT NULL
);

CREATE TABLE IF NOT EXISTS patients (
    id UUID PRIMARY KEY,
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL
);

CREATE TABLE IF NOT EXISTS termins (
    id UUID PRIMARY KEY,
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL,
    status VARCHAR(20) NOT NULL,
    patient_id UUID NOT NULL REFERENCES patients(id),
    therapist_id UUID NOT NULL REFERENCES therapists(id)
);

